######
# Sample "Material Incident" Search Skill 
# An incident has
# - a description of the target material
# - a geographical region (as a spatial segment)
# The skill produces
# - incident site(s) and source part(s)
# - affected product(s) and organization(s)
# - (m)bom trace(s) from source to product 
######

SELECT ?incidentSite ?problemPart ?problemPartName ?product ?productName ?vendor ?lat ?lon  ?part3 ?part4 ?site2 ?site3 ?site4 ?site5 ?lat2 ?lon2 ?lat3 ?lon3 ?lat4 ?lon4 ?lat5 ?lon5 ?partName ?part WHERE {

  VALUES (?material ?latmin ?latmax ?lonmin ?lonmax) { 
      ("@material"^^xsd:string "@latmin"^^xsd:double "@latmax"^^xsd:double "@lonmin"^^xsd:double "@lonmax"^^xsd:double) 
  }

  ## Look for production sites in the region
  ## using the BPNM/Golden Record Catalogue
  ?incidentSite cx:hasGeoInformation [
      cx:hasLatitude ?lat;
      cx:hasLongitude ?lon
  ].
  FILTER( ?lat >=  ?latmin && ?lat <= ?latmax &&
          ?lon >=  ?lonmin && ?lon <= ?lonmax)

  ## Find the connector address of the responsible
  ## businesspartner/orga from the federated data catalogue 
  ?incidentOrga cx:hasSite ?incidentSite;
        cx:hasConnector ?connectorUrl.

  SERVICE ?connectorUrl {

      ## Is there a product which has the incident "material"
      ## workaround: use the part name
       ?part rdf:type cx:Part;
         cx:partName ?partName;
         cx:isProducedBy ?incidentSite.
       FILTER( CONTAINS(?partName, ?material)).

      ?part cx:isPartOf ?problemPart.
      ?problemPart cx:partName ?problemPartName;
             cx:isProducedBy ?site2.

      OPTIONAL {
          ?problemPart cx:isPartOf ?part3.
          ?part3 cx:partName ?part3Name;
                 cx:isProducedBy ?site3.
      }

     OPTIONAL {
          ?part3 cx:isPartOf ?part4.
          ?part4 cx:partName ?part4Name;
                 cx:isProducedBy ?site4.
     }

      OPTIONAL {
          ?part4 cx:isPartOf ?part5.
          ?part5 cx:partName ?part5Name;
                   cx:isProducedBy ?site5.
     }
  }

  BIND(COALESCE(?part5,?part4,?part3,?problemPart) as ?product)
  BIND(COALESCE(?part5Name,?part4Name,?part3Name,?problemPartName) as ?productName)
  BIND(COALESCE(?site5,?site4,?site3,?site2) as ?productSite)

  ?vendor cx:hasSite ?productSite.

  OPTIONAL {
      ?site2 cx:hasGeoInformation [
      cx:hasLatitude ?lat2;
      cx:hasLongitude ?lon2
    ].
  }

  OPTIONAL {
   ?site3 cx:hasGeoInformation [
      cx:hasLatitude ?lat3;
      cx:hasLongitude ?lon3
   ].
  }

  OPTIONAL {
    ?site4 cx:hasGeoInformation [
      cx:hasLatitude ?lat4;
      cx:hasLongitude ?lon4
    ].
  }

  OPTIONAL {
    ?productSite cx:hasGeoInformation [
      cx:hasLatitude ?lat5;
      cx:hasLongitude ?lon5
    ].
  }
}

